import React from 'react';
import { getCart, checkout, getOrders } from '../api/api';

function Header({ token }) {
  const handleCart = async () => {
    try {
      const cart = await getCart(token);
      if (cart.cart_id && cart.item_ids && cart.item_ids.length > 0) {
        const itemIds = cart.item_ids.join(', ');
        window.alert(`Cart ID: ${cart.cart_id}, Item IDs: ${itemIds}`);
      } else {
        window.alert('Cart is empty');
      }
    } catch (error) {
      window.alert('Failed to load cart');
    }
  };

  const handleCheckout = async () => {
    try {
      await checkout(token);
      window.alert('Order successful');
    } catch (error) {
      window.alert('Checkout failed');
    }
  };

  const handleOrderHistory = async () => {
    try {
      const orders = await getOrders(token);
      const orderIds = orders.map((o) => o.id).join(', ');
      window.alert(`Order IDs: ${orderIds || 'No orders'}`);
    } catch (error) {
      window.alert('Failed to load orders');
    }
  };

  return (
    <div>
      <button onClick={handleCheckout}>Checkout</button>
      <button onClick={handleCart}>Cart</button>
      <button onClick={handleOrderHistory}>Order History</button>
    </div>
  );
}

export default Header;
