const API_URL = 'http://localhost:8082';

export const login = async (username, password) => {
  console.log('=== API LOGIN DEBUG START ===');
  console.log('API_URL:', API_URL);
  console.log('Request URL:', `${API_URL}/users/login`);
  console.log('Request body:', JSON.stringify({ username, password }));
  
  const response = await fetch(`${API_URL}/users/login`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ username, password }),
  });

  console.log('Response status:', response.status);
  console.log('Response ok:', response.ok);
  console.log('Response headers:', Object.fromEntries(response.headers.entries()));
  
  const data = await response.json();
  console.log('Response data:', data);
  console.log('Data type:', typeof data);
  console.log('Data.token:', data?.token);

  if (!response.ok) {
    console.log('Response not OK, throwing error');
    throw new Error(data.error || 'Login failed');
  }

  if (!data || !data.token) {
    console.log('No token in data, throwing error');
    throw new Error('No token received');
  }

  console.log('=== API LOGIN DEBUG END - SUCCESS ===');
  return data;
};


export const getItems = async (token) => {
  const response = await fetch(`${API_URL}/items`, {
    headers: {
      'Authorization': `Bearer ${token}`,
    },
  });
  if (!response.ok) {
    throw new Error('Failed to fetch items');
  }
  return response.json();
};

export const addToCart = async (itemIds, token) => {
  const response = await fetch(`${API_URL}/carts`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`,
    },
    body: JSON.stringify({ item_ids: itemIds }),
  });
  if (!response.ok) {
    throw new Error('Failed to add item to cart');
  }
  return response.json();
};

export const getCart = async (token) => {
  const response = await fetch(`${API_URL}/carts`, {
    headers: {
      'Authorization': `Bearer ${token}`,
    },
  });
  if (!response.ok) {
    throw new Error('Failed to fetch cart');
  }
  return response.json();
};

export const checkout = async (token) => {
  const response = await fetch(`${API_URL}/orders`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`,
    },
  });
  if (!response.ok) {
    throw new Error('Failed to checkout');
  }
  return response.json();
};

export const getOrders = async (token) => {
  const response = await fetch(`${API_URL}/orders`, {
    headers: {
      'Authorization': `Bearer ${token}`,
    },
  });
  if (!response.ok) {
    throw new Error('Failed to fetch orders');
  }
  return response.json();
};
